mapper.select:
select ct.id, ct.source_id sourceId, sc.name sourceName, ct.duration duration, ct.create_time createTime, ct.start_time startTime, ct.pause_time pauseTime, ct.stop_time stopTime, ct.state state, ct.finish_time finishTime from collect_task ct left join source_config sc on ct.source_id=sc.id
and ct.state=#{state}
and ct.source_id=#{sourceId}
and ct.create_time>=#{startTime}
and ct.create_time<=#{endTime}
