mapper.select:
select mv.id movieId, mv.mv_name mvName, mv.mv_type categoryId, cg.name categoryName, mv.mv_area mvArea, mv.mv_year mvYear, mv.create_date createDate, mv.update_date updateDate, mv.update_info updateInfo, mv.description description, mv.actor_list actorList, mv.director_list directorList, mv.state state, mv.picture picture, mv.score score from movie mv left join category cg on mv.mv_type=cg.id join role_category rc on mv.mv_type=rc.category_id
rc.role_id=#{roleId}
and mv.mv_name like concat('%',#{mvName},'%')
and mv.mv_type=#{mvType}
and mv.mv_area=#{mvArea}
and mv.mv_year=#{mvYear}
order by update_date desc
